CREATE TABLE `cosme_ebay_listing` (  `BuyerProtection` varchar(50) NOT NULL,  `Currency` varchar(5) NOT NULL,  `ItemID` bigint(20) NOT NULL,  `EndTime` varchar(100) NOT NULL,  `ViewItemURL` text NOT NULL,  `ListingDuration` varchar(50) NOT NULL,  `ListingType` varchar(50) NOT NULL,  `CategoryID` int(8) DEFAULT NULL,  `CategoryName` varchar(255) NOT NULL,  `Quantity` int(8) DEFAULT NULL,  `QuantitySold` int(8) DEFAULT NULL,  `QuantityAvailable` int(8) DEFAULT NULL,  `CurrentPrice` double NOT NULL,  `ShipToLocations` varchar(5) NOT NULL,  `Site` varchar(20) NOT NULL,  `Title` varchar(255) NOT NULL,  `SKU` varchar(255) NOT NULL,  `HitCount` int(8) DEFAULT NULL,  `ConditionID` int(8) DEFAULT NULL,  `ConditionDisplayName` varchar(50) NOT NULL,  `PictureURL` text NOT NULL,  `ShippingProfileID` int(8) DEFAULT NULL,  `ShippingProfileName` varchar(255) NOT NULL,  `ReturnProfileID` int(8) DEFAULT NULL,  `ReturnProfileName` varchar(255) NOT NULL,  `PaymentProfileID` int(8) DEFAULT NULL,  `PaymentProfileName` varchar(255) NOT NULL,  PRIMARY KEY (`ItemID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;

