CREATE TABLE `ebay_categories` (  `CategoryID` int(10) NOT NULL DEFAULT '0',  `CategoryLevel` int(5) NOT NULL DEFAULT '0',  `CategoryName` varchar(120) NOT NULL DEFAULT '',  `CategoryParentID` int(10) NOT NULL DEFAULT '0',  `LeafCategory` int(1) NOT NULL DEFAULT '0',  `AutoPayEnabled` int(1) NOT NULL DEFAULT '0',  `Expired` int(1) NOT NULL DEFAULT '0',  `IntlAutosFixedCat` int(1) NOT NULL DEFAULT '0',  `Virtual` int(1) NOT NULL DEFAULT '0',  `LSD` int(1) NOT NULL DEFAULT '0',  `ORPA` int(1) NOT NULL DEFAULT '0',  `DiscountRate` decimal(5,1) NOT NULL DEFAULT '0.0',  PRIMARY KEY (`CategoryID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8

